var alpage = require('./index.js'),
	log = require('./log.js'),
	assert = require('assert');

alpage("J'adore la chanson que tu as chantée, elle était fantastique.")
	.then(function(tree){
		assert(tree);
		assert(tree.groups.length == 7);
		assert(tree.relations.length == 9);

		console.log("alpage test: OK");
	})
	.catch(function(err){
		assert(!err);
	});