var ml = require('machine_learning');

var training_data = [
    {"color":0, "shape":0, "liked":1},
    {"color":1, "shape":0, "liked":1},
    {"color":0, "shape":1, "liked":2},
    {"color":1, "shape":1, "liked":3},
    {"color":0, "shape":2, "liked":1},
    {"color":1, "shape":2, "liked":1},
    {"color":2, "shape":2, "liked":3},
    {"color":2, "shape":1, "liked":2}
];

var data = [[0, 0],
            [1, 0],
            [0, 1],
            [1, 1],
            [0, 2],
            [1, 2],
            [2, 2],
            [2, 1]]

var result = [1, 1, 2, 3, 1, 1, 3, 2]

var dt = new ml.DecisionTree({
    data : data,
    result : result
});

dt.build();

var predicted_class = dt.classify([90, 1.5]);

console.log(predicted_class);