# -*- coding: latin1 -*-

from __init__ import negate, removePos
import unittest

class TestSequenceFunctions(unittest.TestCase):

    def test_removePos(self):
    	self.assertEqual(removePos("je_CLN"), "je")

    def test_negate(self):
    	self.assertEqual(negate("je_CLN ne_CLNEG suis_V pas_CLN content_ADJ ,__ cela_CLN dit_V"), "je_CLN ne_CLNEG NOT_suis_V pas_CLN NOT_content_ADJ ,__ cela_CLN dit_V")
    	self.assertEqual(negate("cet_CLN article_NC est_V non_CLNEG conforme_ADJ et_CONJ incompatible_ADJ"), "cet_CLN article_NC est_V non_CLNEG NOT_conforme_ADJ et_CONJ incompatible_ADJ")

if __name__ == '__main__':
    unittest.main()