from __init__ import RpcClient
from subprocess import Popen
import unittest

class TestSequenceFunctions(unittest.TestCase):

    def setUp(self):
    	self.client = RpcClient('multiply')
    	self.server = Popen(["python", "test_server.py"])

    def test_shuffle(self):
    	self.assertEqual(self.client.call(4), 16)
    	self.assertEqual(self.client.call(5), 25)

    def tearDown(self):
    	self.server.terminate()

if __name__ == '__main__':
    unittest.main()
