var Debates = require('./index.js'),
	assert = require('assert');

var debates = new Debates("http://www.nosdeputes.fr/14/dossier/8077", "Agriculture alimentation et forêt");

debates.on("structured", function(){
	assert(debates._interventions.length);
	assert(debates._opinions.length);
	assert(debates._opinions[0].name == "Claude Bartolone");
	assert(debates._opinions[0].group == "SRC");
	assert(debates._opinions[0].text.match("La parole est à M. le ministre de l’agriculture, de l’agroalimentaire et de la forêt"));
	assert(debates._interventions.length == 3);


	console.log("rc-debates test: OK");
});

debates.on("err", function(err){
	console.log(err.stack);
});