var connector = require("./index.js"),
	assert = require('assert');

connector.getSummaries()
	.then(function(results){
		var lastLaw = results[0];
		assert.equal(typeof lastLaw.content, "string");
		assert.equal(typeof lastLaw.url, "string");
		assert.ok(Array.isArray(lastLaw.tags));
		assert.equal(typeof lastLaw.anUrl, "string");
		assert.equal(typeof lastLaw.date, "string");
		assert.equal(typeof lastLaw.summary, "string");
		assert.equal(typeof lastLaw.status, "number");

		console.log("vie publique test: OK");
	}).catch(function(err){
		console.log(err.stack);
	});